  //   new Todos("learn redux"),
  // ];
const [todos, settodos] = useState<Todos[]>([]);
  const addTodoHandler = (todo:string)=>{
console.log(todo);
settodos(prevState => prevState.concat());
  }
  return (
    <div>
      <Todo item={todos}></Todo>
      <NewTodo onAddTodo={addTodoHandler}></NewTodo>
    </div>
  );
}

export default App;
